Escape Brackets

    function escapeBrackets() {
        const inputText = document.getElementById('inputText').value;
        const escapedText = inputText.replace(/\[/g, '&lbrack;').replace(/\]/g, '&rbrack;');
        document.getElementById('outputText').value = escapedText;
    }



